import {
  USER_REGISTER_LOADING,
  USER_REGISTER_SUCCESS,
  USER_REGISTER_FAIL,
  USER_LOGIN_LOADING,
  USER_LOGIN_SUCCESS,
  USER_LOGIN_FAIL,
} from "./types";
import {Error} from './errors.actions'
import { loginPost, registerPost } from "../Api/auth.api";
export const registerData = (formData) => async (dispatch) => {
  console.log(formData);
  try {
    dispatch({
      type: USER_REGISTER_LOADING,
    });
    const { data } = await registerPost(formData);
    console.log(data);
    dispatch({
      type: USER_REGISTER_SUCCESS,
      payload: data,
    });
  } catch (error) {
    if(error.response){
      dispatch({
        type: USER_LOGIN_FAIL,
        payload:error.response.data.message
      });
    }
  }
};

export const loginData = (loginformData) => async (dispatch) => {
  try {
    dispatch({
      type: USER_LOGIN_LOADING,
    });
    const { data } = await loginPost(loginformData);
    dispatch({
      type:USER_LOGIN_SUCCESS,
      payload:data
    })
  } catch (error) {
    if(error.response){
      dispatch({
        type: USER_LOGIN_FAIL,
        payload:error.response.data.message
      });
    }
  }
};
